/* empty css     */function r(){const p=document.querySelectorAll(".riple__btn");!p||p.forEach(e=>{e.classList.contains("riple__btn--opacity")?e.onmouseover=function(o){console.log("riple__btn--opacity");const s=o.pageX-e.offsetLeft,t=o.pageY-e.offsetTop;e.style.setProperty("--x",s+"px"),e.style.setProperty("--y",t+"px")}:e.onmousemove=function(o){const s=o.pageX-e.offsetLeft,t=o.pageY-e.offsetTop;e.style.setProperty("--x",s+"px"),e.style.setProperty("--y",t+"px")}})}document.addEventListener("DOMContentLoaded",()=>{r()});
